import pygamefrom tkinter.messagebox import askyesno, showinfopygame.init()win = pygame.display.set_mode((256, 256))pygame.display.set_caption(":DDDDDDDDDDDDDDDDDDDDD")index = 0loop = Trueanimation = [    pygame.image.load('avatars/0.png'),     pygame.image.load('avatars/1.png'),     pygame.image.load('avatars/2.png'),     pygame.image.load('avatars/3.png'),     pygame.image.load('avatars/4.png')]pygame.mixer.music.load('audio.mp3')pygame.mixer.music.play(-1)while loop:    pygame.time.delay(100)    if index == 5:        index = 0    for event in pygame.event.get():        if event.type == pygame.QUIT:            box = askyesno(':(', 'd-do y-you want to lweave? (pwease dont lweave uwu)')            if not box:                showinfo('YAYAAYAYAYAY :D', 'thwanks fowr not cwosing owo')            else:                showinfo('oh :C', 'ok, h-hope you will come s-soon!')                loop = False    win.fill((0,0,0))    win.blit(animation[index], (0, 0))    pygame.display.update()    index += 1pygame.quit()